import React from "react";

const TicketCard = ({ ticket }) => {
  // Map priority to icons
  const getPriorityIcon = (priority) => {
    switch (priority) {
      case "High":
        return "/icons_FEtask/Img-HighPriority.svg";
      case "Medium":
        return "/icons_FEtask/Img-MediumPriority.svg";
      case "Low":
        return "/icons_FEtask/Img-LowPriority.svg";
      case "Urgent":
        return "/icons_FEtask/SVG-UrgentPrioritycaution.svg";
      default:
        return "/icons_FEtask/No-priority.svg";
    }
  };

  // Map status to icons
  const getStatusIcon = (status) => {
    switch (status) {
      case "Backlog":
        return "/icons_FEtask/Backlog.svg";
      case "To Do":
        return "/icons_FEtask/To-do.svg";
      case "In Progress":
        return "/icons_FEtask/in-progress.svg";
      case "Done":
        return "/icons_FEtask/Done.svg";
      case "Cancelled":
        return "/icons_FEtask/Cancelled.svg";
      default:
        return "/icons_FEtask/No-priority.svg";
    }
  };

  return (
    <div className="ticket-card">
      <div className="ticket-header">
        <h3>{ticket.title}</h3>
        <img src="/icons_FEtask/2dotsmenu.svg" alt="Menu" className="action-icon" />
      </div>
      <p className="tag">{ticket.tag}</p>
      <div className="ticket-footer">
        <img src={getStatusIcon(ticket.status)} alt={ticket.status} className="status-icon" />
        <img src={getPriorityIcon(ticket.priority)} alt={ticket.priority} className="priority-icon" />
      </div>
    </div>
  );
};

export default TicketCard;
